    private Step step;

    @BeforeEach
    public void setUp() {
        lenient().when(sessionFactory.openSession()).thenReturn(session);
    }

    @Test
    public void testTC1PostValid() {
        JsonObject jsonRequestRecipe = new JsonObject();
        jsonRequestRecipe.addProperty("title", "Carbonara");
        jsonRequestRecipe.addProperty("description", "Molto facile da preparare");
        jsonRequestRecipe.addProperty("difficulty", "EASY");
        jsonRequestRecipe.addProperty("category", "OMNIVORE");

        InputStream inputStreamRecipe = new ByteArrayInputStream(jsonRequestRecipe.toString().getBytes(StandardCharsets.UTF_8));
        lenient().when(context.getInputStream()).thenReturn(inputStreamRecipe);

        Response responseRecipe = recipeResource.post(context);

        assertEquals(Response.Status.CREATED.getStatusCode(), responseRecipe.getStatus());

        JsonObject jsonRequestStep = new JsonObject();
        jsonRequestStep.addProperty("action", "CUT");
        jsonRequestStep.addProperty("description", "la pancetta");
        jsonRequestStep.addProperty("duration", 2);
        jsonRequestStep.addProperty("stepIndex", 1);

        InputStream inputStreamStep = new ByteArrayInputStream(jsonRequestStep.toString().getBytes(StandardCharsets.UTF_8));
        lenient().when(context.getInputStream()).thenReturn(inputStreamStep);

        Response responseStep = stepResource.post(context, 1);
        assertEquals(Response.Status.CREATED.getStatusCode(), responseStep.getStatus());

        JsonObject jsonRequestPost = new JsonObject();
        jsonRequestPost.addProperty("standardUserId", 1);
        jsonRequestPost.addProperty("postState", "ACTIVE");
        jsonRequestPost.addProperty("postPicture", "data:image/jpeg;base64,");

        InputStream inputStreamPost = new ByteArrayInputStream(jsonRequestPost.toString().getBytes(StandardCharsets.UTF_8));
        lenient().when(context.getInputStream()).thenReturn(inputStreamPost);

        Response responsePost = postResource.post(context);

        assertEquals(Response.Status.CREATED.getStatusCode(), responsePost.getStatus());

        JsonObject jsonObject = new JsonObject();
        jsonObject.addProperty("recipeId", 1);

        InputStream inputStream = new ByteArrayInputStream(jsonObject.toString().getBytes(StandardCharsets.UTF_8));
        lenient().when(context.getInputStream()).thenReturn(inputStream);

        Response response = postResource.put(context, 1);
        assertEquals(Response.Status.OK.getStatusCode(), response.getStatus());

    }

